// Macro to generate a mansion

let rooms = await new Roll(`1d4+2`).evaluate();
let HTML = "<h1 class='centered'>Mansion</h1>";

for (let x= 1; x <= rooms._total; x++) {
  HTML = HTML.concat(`<h3>Room ${x}</h3><br>`);
   const roomTable = game.tables.get("PAjLxVvjlZ51pzpV");
  const roomTableResult= await roomTable.draw({displayChat: false});
  const result = roomTableResult.results.pop().getChatText();
  HTML = HTML.concat(`<p>${result}<p>`);
}

const message = `<div class="shadowdark">${HTML}</div>`;
const chatData = {
	user: game.user._id,
	speaker: ChatMessage.getSpeaker(),
	content: message,
	whisper: game.users.filter(u => u.isGM).map(u => u._id),
};
ChatMessage.create(chatData, {});

