const message = `<div class="shadowdark">
<h3 class="larger">O Slime Lord, open the way before us; Dissolve us in thine glutinous glory</h3>
</div>`;

const chatData = {
	user: game.user._id,
	flavor: "Words in the Pool",
	content: message,
};

ChatMessage.create(chatData, {});

// Macro to generate a random villager.

const nameTable = game.tables.get("WQucE2T70QNZiiU1");
const nameResult = await nameTable.draw({displayChat: false});
const name = nameResult.results.pop().getChatText();

const temperamentTable = game.tables.get("mMbtuC0vZ5o9zUvd");
const temperamentResult = await temperamentTable.draw({displayChat: false});
const temperament = temperamentResult.results.pop().getChatText();

const occupationTable = game.tables.get("R3iVwT2kv92vchS2");
const occupationResult = await occupationTable.draw({displayChat: false});
const occupation = occupationResult.results.pop().getChatText();

const message = `<div class="shadowdark"><h2>${name}</h2><h3>A ${temperament} ${occupation}</h3></div>`;

const chatData = {
	user: game.user._id,
	speaker: ChatMessage.getSpeaker(),
	content: message,
	whisper: game.users.filter(u => u.isGM).map(u => u._id),
};

ChatMessage.create(chatData, {});

