command:
// Use luck token macro
// Author: PrototypeESBU
// v1.0
// -----------------------------

if (game.settings.get("shadowdark",  "usePulpMode")) {
    if (actor.system.luck.remaining > 0) {
        await actor.update({"system.luck.remaining": actor.system.luck.remaining-1});
        sendToChat();
        ui.notifications.info(`${actor.system.luck.remaining} luck tokens remaining`);
    }
    else {
        ui.notifications.error(`${actor.name} has no luck tokens`);
    }
}
else {
    if (actor.system.luck.available) {
		await actor.update({"system.luck.available": false});
        sendToChat();
    }
    else {
        ui.notifications.error(`${actor.name} has no luck token`);
    }
}

function sendToChat() {
    ChatMessage.create({
            classes: ["shadowdark"],
            flavor: `Using luck token`,
            content: `<h3 style="font-family:'Montserrat-Bold'">${actor.name} used a luck token</h3>`,
        });
}
